schemas.zodDefinition:
const approveRoutingSchema = z.object({
  routingId: z.string().uuid(),
  approvedBy: z.string().min(1),
  notes: z.string().optional()
})

const resequenceStepsSchema = z.object({
  routingId: z.string().uuid(),
  stepOrder: z.array(z.object({
    stepId: z.string().uuid(),
    newStepNumber: z.number().int().positive()
  })

const approvalActionSchema = z.object({
  action: z.nativeEnum(ApprovalAction),
  comments: z.string().optional(),
  signatureId: z.string().optional(),
  signatureType: z.string().optional()
})

const delegationSchema = z.object({
  delegateeId: z.string().uuid(),
  reason: z.string().min(10, 'Reason must be at least 10 characters'),
  expiry: z.string().datetime().optional().transform(val => val ? new Date(val) : undefined)
})

const assignmentSchema = z.object({
  assignedToId: z.string().uuid(),
  assignedToRole: z.string().optional(),
  assignmentType: z.string(),
  dueDate: z.string().datetime().optional().transform(val => val ? new Date(val) : undefined)
})

const bulkActionSchema = z.object({
  assignmentIds: z.array(z.string().uuid()),
  action: z.nativeEnum(ApprovalAction),
  comments: z.string().optional()
})

const updateStatusSchema = z.object({
  status: z.enum(['OPERATIONAL', 'MAINTENANCE', 'DOWN']),
  notes: z.string().optional()
})

const scheduleMaintenanceSchema = z.object({
  maintenanceDate: z.string().datetime(),
  notes: z.string().optional()
})

const bulkUpdateSchema = z.object({
  documentIds: z.array(z.string()).min(1, 'At least one document ID is required'),
  updates: z.object({
    tags: z.array(z.string()).optional(),
    categories: z.array(z.string()).optional(),
    status: z.string().optional()
  })

const getStatisticsSchema = z.object({
  startDate: z.string().datetime(),
  endDate: z.string().datetime(),
  documentType: z.string().optional()
})

const contentChangeSchema = z.object({
  documentType: z.string().min(1),
  documentId: z.string().min(1),
  changeData: z.any()
})

const completeReviewSchema = z.object({
  outcome: z.enum(['APPROVED', 'REJECTED', 'NEEDS_CHANGES']),
  comments: z.string().optional(),
  checklist: z.array(z.object({
    id: z.string(),
    completed: z.boolean(),
    notes: z.string().optional()
  })

const startSessionSchema = z.object({
  documentType: z.string().min(1, 'Document type is required'),
  documentId: z.string().min(1, 'Document ID is required'),
  sessionData: z.any().optional(),
  metadata: z.any().optional()
})

const resolveConflictSchema = z.object({
  conflictId: z.string().min(1, 'Conflict ID is required'),
  resolutionStrategy: z.nativeEnum(ResolutionStrategy),
  resolutionData: z.any(),
  notes: z.string().optional()
})

const subscribeSchema = z.object({
  documentType: z.string().min(1, 'Document type is required'),
  documentId: z.string().min(1, 'Document ID is required'),
  subscriptionTypes: z.array(z.string()).min(1, 'At least one subscription type is required'),
  preferences: z.any().optional()
})

const contentChangeSchema = z.object({
  documentType: z.string().min(1),
  documentId: z.string().min(1),
  changeData: z.any()
})

const collaborationStatsSchema = z.object({
  startDate: z.string().datetime().optional(),
  endDate: z.string().datetime().optional()
})

const clockOutSchema = z.object({
  clockOutTime: z.string().datetime().optional(),
})

const editTimeEntrySchema = z.object({
  clockInTime: z.string().datetime().optional(),
  clockOutTime: z.string().datetime().optional(),
  editReason: z.string().min(1),
})

const createGenealogySchema = z.object({
  parentSerialNumber: z.string(),
  componentSerialNumber: z.string(),
  assemblyDate: z.string().datetime().optional(),
  assemblyOperator: z.string().optional(),
})

const exportAnnotationsSchema = z.object({
  format: z.enum(['JSON', 'CSV']).optional().default('JSON')
})

const replyCommentSchema = z.object({
  parentCommentId: z.string().min(1, 'Parent comment ID is required'),
  commentText: z.string().min(1, 'Comment text is required').max(10000, 'Comment text too long'),
  attachments: z.array(z.string()).optional(),
  mentionedUserIds: z.array(z.string()).optional()
})

const reactionSchema = z.object({
  reactionType: z.nativeEnum(ReactionType)
})

const AnnotationSchema = z.object({
  id: z.string(),
  type: z.enum(['arrow', 'callout', 'highlight', 'circle', 'rectangle', 'text']),
  position: z.object({
    x: z.number(),
    y: z.number(),
    width: z.number().optional(),
    height: z.number().optional()
  })

const UpdateMetadataSchema = z.object({
  title: z.string().optional(),
  description: z.string().optional(),
  tags: z.array(z.string()).optional()
})

const loginSchema = z.object({
  username: z.string().min(1, 'Username is required'),
  password: z.string().min(1, 'Password is required')
})

const refreshTokenSchema = z.object({
  refreshToken: z.string().min(1, 'Refresh token is required')
})

const discoverProvidersSchema = z.object({
  email: z.string().email('Valid email required')
})

const initiateAuthSchema = z.object({
  email: z.string().email('Valid email required'),
  preferredProviderId: z.string().optional(),
  forceProvider: z.boolean().default(false),
  returnUrl: z.string().url().optional()
})

const callbackSchema = z.object({
  success: z.boolean(),
  userProfile: z.object({
    email: z.string().email(),
    firstName: z.string().optional(),
    lastName: z.string().optional(),
    given_name: z.string().optional(),
    family_name: z.string().optional()
  })

const querySchema = z.object({
  siteId: z.string().uuid().optional(),
  limit: z.string().regex(/^\d+$/).transform(Number).optional()
})

const updatePresenceSchema = z.object({
  resourceType: z.enum(['routing', 'routing-step', 'work-order']),
  resourceId: z.string().min(1),
  action: z.enum(['viewing', 'editing']),
  userName: z.string().optional(), // Will use req.user if not provided
})

const getPresenceSchema = z.object({
  resourceType: z.enum(['routing', 'routing-step', 'work-order']),
  resourceId: z.string().min(1),
})

const removePresenceSchema = z.object({
  resourceType: z.enum(['routing', 'routing-step', 'work-order']),
  resourceId: z.string().min(1),
})

const updateStatusSchema = z.object({
  status: z.string(),
  location: z.string().optional(),
})
